import React from 'react';

export default class AddingModelFilter extends React.Component{
    constructor(props){
        super(props);
        this.state={
            List:[],
            Selected:null,
        }
        this.fetchData = this.fetchData.bind(this);
    }

    componentDidMount(){

    }
    async fetchData(){
        const _this = this;
        let endpoint = "/crum";

        let data = this.state;
        const hub = {
            OrgCode: this.state.OrgCode,
            ServiceName: "select",
            TableName: this.props.ModelName,
            Data: data,
            ParamUpdate: [],
            ParamConditions: []
        }


    }

    handleInput=(e)=>{
        this.setState({Selected:e.target.value});
    }
    getValue=()=>{
        if(this.state.Selected !==null){
            return this.state.Selected;
        }
        return "";
    }

    renderOptions=()=>{

    }

    render(){
        return(
            <div className="form-group">
                <label>{this.props.row.desc}</label>
                <select
                    type={this.props.row.type}
                    className="form-control"
                    id={this.props.row.field}
                    aria-describedby={this.props.row.field + "Help"}
                    placeholder={this.props.row.placeholder}
                    required={this.props.row.required}
                    onChange={(e) => this.handleInput(e, this.props.row.field)}
                    value={this.getValue(this.props.row.field)}
                >
                    {this.renderOptions()}
                </select>

            </div>
        )
    }
}
