import logoImg from "../../asset/img/logo.jpeg";
import {Button, ButtonToolbar} from "react-bootstrap";
import React from "react";
//https://fontawesome.bootstrapcheatsheets.com/#
import ButtonUI from '@material-ui/core/Button';

const PageHeading = (props) => {

    const getUserName=()=>{

        if(props.login.User !==null){
            return props.login.User.Name;
        }

        return "Guest"
    }

    const loggedOut=()=>{

    }

    const menusButtons=()=>{

        const ls=[
            {menu:"category",display:"V. Category"},
            {menu:"vehicle",display:"Vehicle"},
            {menu:"driver",display:"Driver"},
            {menu:"task",display:"Task"},
        ];

        return ls.map((row,index)=>{
            return (
                <ButtonUI key={index+"#"+row.menus} variant="contained" color="primary">
                    {row.display}
                </ButtonUI>
            );
        })
    }
    return (
        <div className={"pageHeader"}>
            <div className={"pageHeaderModule"}>
                <img className={"logoCompany"} src={logoImg} />
                <span className={"companyTitle"}>Easipath </span>
                <small>Fleet Management System</small>
                <div className={"pageHeaderModuleLinkBox"}>
                    <small>
                       <i className={"fa fa-user"} ></i>&nbsp;
                    {getUserName()}
                        <a className={"headerLogout"} onClick={()=>{loggedOut()}}>Logout</a>
                    </small>
                    &nbsp;&nbsp;&nbsp;
                    <a className="myLinkHeader">Home</a>|<a className="myLinkHeader">Collection</a>|<a className="myLinkHeader">Dashboard</a>
                </div>
            </div>
            <div className={"headerLine"}>&nbsp;</div>
            <div className={"pageHeaderMenu"}>
                <ButtonToolbar>
                    <a className={"headerMenuSelectSecondBar"}>Menu: </a>
                    {menusButtons()}
                    <Button>Category</Button>
                    <Button >Vehicle</Button>
                    <Button >Driver</Button>
                    <Button>Task</Button>
                    {/*<Button bsStyle="info">Task</Button>*/}
                </ButtonToolbar>
            </div>
        </div>
    )
}

export default PageHeading;
