import React, {Component} from 'react';


import logoImg from '../../asset/img/logo.jpeg';
import {ButtonToolbar,Button} from 'react-bootstrap';
import "../../asset/css/wallpaper.css"


const PageHeading = (props) => {

    return (
        <div className={"pageHeader"}>
            <div className={"pageHeaderModule"}>
                <img className={"logoCompany"} src={logoImg} />
                <span className={"companyTitle"}>Easipath </span>
                <small>Fleet Management System</small>
                <div className={"pageHeaderModuleLinkBox"}>
                    <a className="clickMe myLinkHeader" onClick={()=>alert("Clicked me")}>Home</a>|<a>Collection</a>|<a>Dashboard</a>
                </div>
            </div>
            <div className={"pageHeaderMenu"}>
                <ButtonToolbar>
                    {/* Standard button */}
                    <Button>Default</Button>

                    {/* Provides extra visual weight and identifies the primary action in a set of buttons */}
                    <Button bsStyle="primary">Primary</Button>

                    {/* Indicates a successful or positive action */}
                    <Button bsStyle="success">Success</Button>

                    {/* Contextual button for informational alert messages */}
                    <Button bsStyle="info">Info</Button>

                    {/* Indicates caution should be taken with this action */}
                    <Button bsStyle="warning">Warning</Button>

                    {/* Indicates a dangerous or potentially negative action */}
                    <Button bsStyle="danger">Danger</Button>

                    {/* Deemphasize a button by making it look like a link while maintaining button behavior */}
                    <Button bsStyle="link">Link</Button>
                </ButtonToolbar>
            </div>
        </div>
    )
}
export default class Wallpaper extends Component {

    constructor(props){
        super(props)
    }
    render() {
        return (
            <div className="wallpaper">
                <div className={"overlay"}>
                    <PageHeading {...this.props}/>
                    {this.props.children}
                </div>
            </div>

        );
    }
}
