import React from 'react';
import ReactHLS from 'react-hls';
import ReactPlayer from "react-player";

export default class MediaStreaming extends React.Component {
    constructor(props) {
        super(props);

        //this.video = window.document.querySelector("#videoElement");

    }

    handleVideo(stream) {
        // this.video.src = (window.URL || window.webkitURL).createObjectURL(stream);
    }

    render() {


        return (
            <div className="container">


                <div className="container">
                    <form id="player-form">
                        <div className="input-group url-control">
                            <input className="form-control" id="player-src" value=""
                                   placeholder="rtmp://www.example.com/live/stream" required="" type="text"/>
                            <span className="input-group-btn">
<button type="submit" className="btn btn-success" id="player-start">Play</button>
</span>
                        </div>
                    </form>
                    <div id="player-container">
                        <object type="application/x-shockwave-flash" id="player"
                                data="../asset/plugin/hls.swf" style={{"visibility": "visible"}} width="640"
                                height="480">
                            <param name="allowFullScreen" value="true"/>
                            <param name="allowScriptAccess" value="always"/>
                            <param name="wmode" value="opaque"/>
                            <param name="flashvars"
                                   value="autoPlay=true&amp;src=rtmp://156.38.129.58/app/live&amp;streamType=live&amp;scaleMode=letterbox"/>
                        </object>
                        <div id="player-tip" style={{"display": "none"}}></div>
                    </div>
                </div>

                <div className="flowplayer" data-rtmp="rtmp://156.38.129.58/app/live">

                    {/* <video controls
                           autoPlay={true}
                           src="rtmp://156.38.129.58/app/live"
                           width="300"
                           height="200">
                        Sorry, your browser doesn't support embedded videos.
                    </video>*/}

                </div>


            </div>
        )
    }
}
