import React from 'react';
import {PostMainPMIS,PostGovPMIS,PostRecordPMIS} from '../../api/auth';

export default class FilteringContract extends React.Component{

    constructor(props){
        super(props);

        this.state={
            province:"",
            municipality:"",
            company:"",
            contract:"",
            ListContract:[],
            ListOrganization:[],
            ComboProvince:[],
        }
    }
   async componentDidMount(){
        await this.fetchContract();
        await this.fetchOrganization();
    }

    fetchContract=async ()=>{
        let _this = this;

        let hub = {

        }
        const endpoint = "/api/contract/list"; // "/api/contracts/information/list
        await PostMainPMIS(hub, endpoint, function(data){

            let lsProvince=[];
            for(let i in data){
                const row = data[i];
                lsProvince.push(row.Province);
            }
            _this.setState({ListContract: data,ComboProvince:lsProvince});

            console.log(":> ListContract > ",lsProvince,data);
        })

    }
    fetchOrganization=async ()=>{
        let _this = this;

        let hub = {

        }
        const endpoint = "/api/organisation/list";
        await PostMainPMIS(hub, endpoint, function(data){
            console.log(":> ListOrganization > ",data);
            _this.setState({ListOrganization: data})
        })

    }




    render(){
        return(
            <div className="">
                filtering request
            </div>
        )
    }
}
