import React,{Component} from 'react';
import '../asset/css/login.css';
import {PostRequestHTTP} from "../api/services";


export default class LoginComponent extends Component {

    constructor(props){
        super(props)
        this.state={

        }

        this.submitForm = this.submitForm.bind(this);
    }

    handleInput = (e, key) => {
        const val = e.target.value;
        this.setState({[key]: val});
    }

    async submitForm(e) {
        e.preventDefault();
        if (this.state.password !== this.state.confirmPassword) {
            alert("Error ! Password doesn't match");
            return
        }

        const cleanInputUsername = (inData) => {
            let data = inData.replace(" ", "");
            data = data.toLocaleLowerCase();
            return data;
        }
        let endpoint = "/user/register";

        let data = this.state;
        data.username = data.email;
        data.username = cleanInputUsername(this.state.username);
        data.password = this.state.password.trim();

        const hub={
            Data: data
        }

        console.log("submitLogin > ", hub, endpoint);

        await PostRequestHTTP(hub, endpoint, function (data) {
            console.log("submitForm response > ", data);
            if (data.RESULT === "OK") {
                alert("Your account have been created, you can now login or wait for activation!");
                window.location.href = "/login"

            } else {
                alert("Account creation Failed:  " + data.RESULT);
            }

        });

    }


    navigatorLink=(myLink)=>{
        window.location.href="/#"+myLink
    }
    render() {
        return (


                <div className="container loginContentBox">
                    <div className="row">
                        <div className="col-md-6 col-md-offset-3">
                            <div className="panel panel-login">
                                <div className="panel-heading">
                                    <div className="row">
                                        <div className="row">
                                            <div className="col-xs-12 myTitleLogin">
                                            Fleet Management System
                                            </div>
                                        </div>
                                        <div className="col-xs-6">
                                            <a href="#" className="active" id="login-form-link">Login</a>
                                        </div>
                                        <div className="col-xs-6">
                                            <a onClick={()=>this.navigatorLink("register")} id="register-form-link" className={"clickMe"}>Register</a>
                                        </div>
                                    </div>
                                    <hr />
                                </div>
                                <div className="panel-body">
                                    <div className="row">
                                        <div className="col-lg-12">
                                            <form id="login-form" action="https://phpoll.com/login/process"
                                                  method="post" role="form" style={{"display": "block"}}>
                                                <div className="form-group">
                                                    <input type="text" name="username" id="username" tabIndex="1"
                                                           className="form-control" placeholder="Username" value="" />
                                                </div>
                                                <div className="form-group">
                                                    <input type="password" name="password" id="password" tabIndex="2"
                                                           className="form-control" placeholder="Password" />
                                                </div>
                                                <div className="form-group text-center">
                                                    <input type="checkbox" tabIndex="3" className="" name="remember"
                                                           id="remember" />
                                                        <label htmlFor="remember"> Remember Me</label>
                                                </div>
                                                <div className="form-group">
                                                    <div className="row">
                                                        <div className="col-sm-6 col-sm-offset-3">
                                                            <input type="submit" name="login-submit" id="login-submit"
                                                                   tabIndex="4" className="form-control btn btn-login"
                                                                   value="Log In" />
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="form-group">
                                                    <div className="row">
                                                        <div className="col-lg-12">
                                                            <div className="text-center">
                                                                <a href="https://phpoll.com/recover" tabIndex="5"
                                                                   className="forgot-password">Forgot Password?</a>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </form>
                                            <form id="register-form" action="https://phpoll.com/register/process"
                                                  method="post" role="form" style={{"display": "none"}}>
                                                <div className="form-group">
                                                    <input type="text" name="username" id="username" tabIndex="1"
                                                           className="form-control" placeholder="Username" value="" />
                                                </div>
                                                <div className="form-group">
                                                    <input type="email" name="email" id="email" tabIndex="1"
                                                           className="form-control" placeholder="Email Address"
                                                           value="" />
                                                </div>
                                                <div className="form-group" >
                                                    <input type="password" name="password" id="password" tabIndex="2"
                                                           className="form-control" placeholder="Password" />
                                                </div>
                                                <div className="form-group">
                                                    <input type="password" name="confirm-password" id="confirm-password"
                                                           tabIndex="2" className="form-control"
                                                           placeholder="Confirm Password" />
                                                </div>
                                                <div className="form-group">
                                                    <div className="row">
                                                        <div className="col-sm-6 col-sm-offset-3">
                                                            <input type="submit" name="register-submit"
                                                                   id="register-submit" tabIndex="4"
                                                                   className="form-control btn btn-register"
                                                                   value="Register Now" />
                                                        </div>
                                                    </div>
                                                </div>
                                            </form>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

            </div>



        );
    }
}
