export default class MediaStreaming extends React.Component {
    constructor(props) {
        super(props);

        //this.video = window.document.querySelector("#videoElement");

    }


     playRTMP(src) {
        const $ =window.$;
        $('#player-tip').hide();

        if (isMobile()) {
            $('#player-tip').html("RTMP protocol is not supported on your device.");
            return;
        }

        var flashvars = {
            autoPlay : 'true',
            src : escape(src),
            streamType : 'live',
            scaleMode : 'letterbox',
        };
        var params = {
            allowFullScreen : 'true',
            allowScriptAccess : 'always',
            wmode : 'opaque'
        };
        var attributes = {
            id : 'player'
        };

        swfobject.embedSWF('../asset/plugin/hls.swf', 'player', '640', '480', '10.2', null, flashvars, params,
            attributes);
    }


    render() {

        this.playRTMP("rtmp://156.38.129.58/app/live");
        return (
            <div className="container">


                <div className="container">

                    <div id="player-container">
                        <object
                            type="application/x-shockwave-flash"
                            id="player"
                            data="../asset/plugin/hls.swf"
                            style={{"visibility": "visible"}} width="640"
                            height="480"
                            autoPlay={true}
                        >
                            <param name="allowFullScreen" value="true"/>
                            <param name="allowScriptAccess" value="always"/>
                            <param name="wmode" value="opaque"/>
                            <param name="flashvars"
                                   value="autoPlay=true&amp;src=rtmp://156.38.129.58/app/live&amp;streamType=live&amp;scaleMode=letterbox"/>
                        </object>
                        <div id="player-tip" style={{"display": "none"}}></div>
                    </div>
                </div>

                <div className="flowplayer" data-rtmp="rtmp://156.38.129.58/app/live">

                    {/* <video controls
                           autoPlay={true}
                           src="rtmp://156.38.129.58/app/live"
                           width="300"
                           height="200">
                        Sorry, your browser doesn't support embedded videos.
                    </video>*/}

                </div>


            </div>
        )
    }
}
