import React from 'react';
import {PostMainPMIS} from "../../api/auth";

export default class DatabaseComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            Employees: [],
            currentEmployee:null,
        }

        this.fetchEmployees = this.fetchEmployees.bind(this);
    }

   async componentDidMount(){
        this.fetchEmployees()
    }

    actionViewEmployee(row) {
        let tmp = this.state;
        tmp.currentEmployee = row;
        this.setState(tmp);
        this.openModal()
        // alert("View company request popup");

    }

    fetchEmployees=async ()=>{
        let _this = this;
        let company = this.props.state.company;
        let endpoint = "/employee/list/" + company;
        let hub = {};
        hub.company = company;

        await PostMainPMIS(hub, endpoint, function (data) {
            let emp = [];

            for (let i in data) {
                let row = data;
                row.Id = parseInt(row.Id);
                emp.push(row);
            }

            _this.setState({Employees: emp});

            console.log(":> fetchEmployees > ", emp);
        })


    }

    renderData=()=>{

        return (
            <div className={"row"}>
                <label className="alert alert-primary companyList">
                    Available Employees List (<span className="badge"> {this.state.Employees.length}</span>)
                </label>

                <table className={"table"} style={{width: "100%"}}>
                    <thead>
                    <tr>
                        <th>Code</th>
                        <th>Fullname</th>
                        <th>IDNo</th>
                        <th>Site</th>
                        <th>Gender</th>
                        <th>Designation</th>
                        <th>Created</th>
                        <th></th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>

                    {this.state.Employees.map((row) => {
                        return (
                            <tr key={row.Id}>
                                <td>{row.EmployeeCode}</td>
                                <td>{row.Name} {row.Surname}</td>
                                <td>{row.EmployeeIDNo}</td>
                                <td>{row.Site}</td>
                                <td>{row.Gender}</td>
                                <td>{row.Designation}</td>
                                <td>{row.OrgDateTime}</td>
                                <td>
                                    <button className={"clickMe btn btn-primary"} onClick={() => {
                                        this.actionViewEmployee(row)
                                    }}>View
                                    </button>
                                    &nbsp;
                                </td>
                                <td>
                                    <button className={"clickMe btn btn-danger"} onClick={() => {
                                        alert("Feature not implemented")
                                    }}>Delete
                                    </button>
                                </td>
                            </tr>
                        )
                    })}
                    </tbody>
                </table>
            </div>
        )
    }



    render() {
        return (
            <div className={""}>
                <div>
                    <hr/>
                    <div className={"row"}>
                        <div className={"col col-lg-12"}>
                            <p className={""}>
                                <button
                                    className={"btn btn-dander"}
                                    onClick={() => this.props.clearNewFilter()}>
                                    CLear [New Filter]
                                </button>
                            </p>
                        </div>
                    </div>

                    <div className={"row"}>
                        <div className={"col col-lg-12"}>
                            {this.renderData}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
