import React, {Component} from 'react';
import '../../asset/css/login.css';
import PageContentWallpaper from '../../component/common/ef-pageContent';

import 'react-animated-slider/build/horizontal.css';
import 'normalize.css/normalize.css';
import '../../asset/css/silder-animations.css';
import '../../asset/css/home-comp-main.css'

import FilteringCompent from './filtering';

export default class HomeComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            province: "",
            municipality: "",
            company: "",
            contract: "",
        }
    }

    async componentDidMount(){
        await this.fetchContract();
    }

    handleInput = (e, key) => {
        this.setState({[key]: e.target.value})
    }

    async fetchContract(){
        let endpoint = "/api/company/list";
        let url = serverEpwp + endpoint;
        let hub = {};
        hub.company = this.state.company;
        let _this = this;

        fetch(url, {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(hub)
        })
            .then((response) => response.json())
            .then((responseData) => {
                var tmp = _this.state;
                tmp[tmpKey] = responseData;
                _this.setState(tmp);
                console.log("+++++> global loadList response ",  responseData);
            });
    }


    render() {
        return (


            <PageContentWallpaper {...this.props} >
                <div className="wrapper">
                    <div className="col-xs-12">
                    <FilteringCompent
                    state={this.state}
                    handleInput={this.handleInput}
                    />

                    </div>
                </div>
            </PageContentWallpaper>


        );
    }
}
