import   {SAVE_NAVIGATIONS} from '../constant';
const initial ={
    home:{desc:"Home",data:[
        {menu: "home", display: "Home"},
        {menu: "collection", display: "Collection"},
        {menu: "dashboard", display: "Dashboard"},
        {menu: "task", display: "Task"},
    ]},
    collection:{desc:"Collection",data:[
        {menu: "checkin", display: "Check-In"},
        {menu: "checkout", display: "Check-Out"},
    ]},
    dashboard:{desc:"Dashboard",data:[]},
}


const reduceNavigations = (state = initial, action) => {
    let newState = state;
    switch (action.type) {
        case SAVE_NAVIGATIONS:
            newState = action.payload;
            return Object.assign({}, newState);
        default:
            return state
    }
}

export default reduceNavigations;
